		{% if not is_popup %}
		{% block adminmenu %}
		{% wpadmin_render_left_menu %}
		{% endblock%}
		{% endif %}

		<!-- Container -->
		<div id="container">

			{% if not is_popup %}
			{% block adminbar %}
			{% wpadmin_render_top_menu %}
