{% load i18n %}

{% wpadmin_are_breadcrumbs_enabled %}{% wpadmin_is_left_menu_enabled %}

<body
	class="{% if is_popup %}popup {% endif %}{% if not wpadmin_are_breadcrumbs_enabled %}wp-no-breadcrumbs {% endif %}{% if wpadmin_is_left_menu_enabled %}wp-with-left-menu {% endif %}{% block bodyclass %}{% endblock %} wp-pinned">

	<!-- Wrapper -->
	<div id="wrapper">
		{% if not is_popup %}
		{% block adminmenu %}
		{% wpadmin_render_left_menu %}
		{% endblock%}
		{% endif %}

		<!-- Container -->
		<div id="container">

			{% if not is_popup %}
			{% block adminbar %}
			{% wpadmin_render_top_menu %}
			{% endblock %}
			{% block breadcrumbs %}
			<div class="breadcrumbs">
				<a href="{% url 'admin:index' %}">{% trans 'Home' %}</a>
				{% if title %} &rsaquo; {{ title }}{% endif %}
			</div>
			{% endblock %}
			{% endif %}

			{% block messages %}
			{% if messages %}
			<ul class="messagelist">
				{% for message in messages %}
				<li{% if message.tags %} class="{{ message.tags }}" {% endif %}>{{ message|capfirst }}</li>
					{% endfor %}
			</ul>
			{% endif %}
			{% endblock messages %}

			<!-- Content -->
			<div id="content" class="{% block coltype %}colM{% endblock %}">
				{% block pretitle %}{% endblock %}
				{% block content_title %}{% if title %}<h1>{{ title }}</h1>{% endif %}{% endblock %}
				{% block content %}
				{% block object-tools %}{% endblock %}
				{{ content }}
				{% endblock %}
				{% block sidebar %}{% endblock %}
				<br class="clear" />
			</div>
			<!-- END Content -->

			{% block footer %}<div id="footer"></div>{% endblock %}
		</div>
		<!-- END Container -->

	</div>
	<!-- END Wrapper -->

</body>

</html>

