{% block content_media %}
<style>
    .title-div {
        margin-top: 24px;
    }
    .title-area {
        margin-bottom: 0;
        margin-top: 0;
    }
    .title-div .title {
        margin-top: 0;
    }
    .status-card {
        background: white;
        border-radius: 12px;
        box-shadow: 0 4px 6px rgba(0, 0, 0, 0.05);
        margin-bottom: 20px;
        overflow: hidden;
        padding: 20px 30px;
    }

    .card-header {
        padding-bottom: 10px;
        border-bottom: 1px solid var(--border-color);
    }

    .card-title {
        font-size: 18px;
        font-weight: 600;
        color: var(--font-basic-black);
        margin: 0;
        font-family: Pretendard, sans-serif;
    }

    .card-content {
        padding-top: 10px;
    }

    .summary-grid {
        display: flex;
        flex-direction: column;
        gap: 8px;
        margin-bottom: 12px;
    }

    .summary-item {
        display: flex;
        align-items: center;
        gap: 8px;
    }

    .summary-label {
        color: var(--font-basic-black);
        font-size: 14px;
    }

    .summary-value {
        font-weight: 500;
        color: var(--font-black);
    }

    .summary-value.passed {
        color: #4CAF50;
    }

    .summary-value.failed {
        color: #F44336;
    }

    .summary-value.score {
        font-size: 14px;
        font-weight: 600;
    }

    .warning-text {
        background: rgba(28, 59, 96, 0.06);
        border: 1px solid var(--border-color);
        border-radius: 8px;
        padding: 16px;
        color: #856404;
        font-family: 'Courier New', monospace;
        font-size: 14px;
        line-height: 1.5;
        white-space: pre-wrap;
    }

    .action-buttons {
        display: flex;
        gap: 12px;
        justify-content: flex-end;
    }

    .action-btn {
        padding: 6px 16px;
        border-radius: 6px;
        border: none;
        font-size: 14px;
        font-weight: 400;
        cursor: pointer;
        text-decoration: none;
        display: inline-flex;
        align-items: center;
        gap: 6px;
        transition: all 0.2s ease;
        background: #edf3fb;
        color: #5285c4;
    }

    .action-btn:hover {
        background: #e3eaf6;
        color: #6291d0;
        text-decoration: none;
    }

    .testcase-table {
        width: 100%;
        border-collapse: separate;
        border-spacing: 0;
        background: white;
        border-top-left-radius: 8px;
        border-top-right-radius: 8px;
        border: 1px solid var(--border-color);
    }

    .testcase-table th {
        background: #f8f9fa;
        padding: 6px 16px;
        text-align: center;
        font-weight: 600;
        color: #495057;
        border-bottom: 1px solid var(--border-color);
        font-size: 14px;
    }

    .testcase-table th:first-child {
        border-top-left-radius: 8px;
    }

    .testcase-table th:last-child {
        border-top-right-radius: 8px;
    }

    .testcase-table td {
        padding: 12px 16px;
        border-bottom: 1px solid var(--border-color);
        font-size: 14px;
        vertical-align: middle;
        text-align: center !important;
        padding: 3px 0 !important;
    }

    #test-cases .case-row td:nth-child(3) span {
        float: none !important;
        font-weight: 500;
        font-size: 13px;
    }

    #test-cases table td.case-number {
        text-align: left !important;
        padding-left: 6px !important;
    }

    .testcase-table tr:last-child td {
        border-bottom: none;
    }

    .testcase-row {
        position: relative;
        transition: background-color 0.2s ease;
    }

    .testcase-row:hover {
        background-color: #f8f9fa;
    }

    .testcase-row {
        background-color: white;
    }

    .status-ac, .AC {
        color: #4CAF50;
        font-weight: 600;
    }

    .status-wa, .WA {
        color: #F44336;
        font-weight: 600;
    }

    .status-tle, .TLE,
    .status-rte, .RTE,
    .status-mle, .MLE {
        color: #FFB300;
        font-weight: 600;
    }

    .status-ole, .OLE,
    .status-ir, .IR {
        color: #546E7A;
        font-weight: 600;
    }

    .status-ab, .AB {
        color: #6c757d;
        font-weight: 600;
    }

    .case-number {
        font-weight: 600;
        color: #495057;
    }

    .execution-time {
        color: #6c757d;
        font-family: 'Courier New', monospace;
    }

    .memory-usage {
        color: #6c757d;
        font-family: 'Courier New', monospace;
    }

    .testcase-table td.score-points {
        font-weight: 400;
        color: #495057;
        font-size: 13px;
    }

    .processing-message {
        text-align: center;
        padding: 40px 20px;
        color: #6c757d;
        font-size: 16px;
    }

    .spinner {
        display: inline-block;
        width: 20px;
        height: 20px;
        border: 3px solid #f3f3f3;
        border-top: 3px solid #5C95D9;
        border-radius: 50%;
        animation: spin 1s linear infinite;
        margin-right: 8px;
    }

    @keyframes spin {
        0% { transform: rotate(0deg); }
        100% { transform: rotate(360deg); }
    }
</style>

{% endblock %}

{% block content_js_media %}
