        min_length=4,
        max_length=50,
        help_text=_('ì•”í˜¸í™” í‚¤ëŠ” 4-50ì ì‚¬ì´ì—¬ì•¼ í•©ë‹ˆë‹¤.')
    )

    def __init__(self, *args, **kwargs):
