            # ì•”í˜¸í™” í‚¤ ë³µì¡ì„± ê²€ì¦
            if len(encryption_key) > 50:
                raise forms.ValidationError('ì•”í˜¸í™” í‚¤ëŠ” ìµœëŒ€ 50ìê¹Œì§€ í—ˆìš©ë©ë‹ˆë‹¤.')

        # ì´ë¯¸ ì•”í˜¸í™”ëœ ë¬¸ì œë¥¼ ì•”í˜¸í™” í•´ì œí•˜ë ¤ëŠ” ê²½ìš°
        elif was_encrypted and not is_encrypted:
            if not encryption_key:
                raise forms.ValidationError('ì•”í˜¸í™”ëœ ë¬¸ì œë¥¼ ë³µí˜¸í™”í•˜ë ¤ë©´ ë³µí˜¸í™” í‚¤ë¥¼ ì…ë ¥í•´ì•¼ í•©ë‹ˆë‹¤.')

            # í‚¤ í•´ì‹œ ê²€ì¦
            try:
                input_hash = hashlib.sha256(encryption_key.encode('utf-8')).hexdigest()
                db_hash = self.instance.encryption_key_hash or ''

                if not db_hash or input_hash != db_hash:
                    raise forms.ValidationError('ì˜ëª»ëœ ë³µí˜¸í™” í‚¤ì…ë‹ˆë‹¤. ì •í™•í•œ ë³µí˜¸í™” í‚¤ë¥¼ ì…ë ¥í•˜ì„¸ìš”.')
            except forms.ValidationError:
                raise
            except Exception as e:
                raise forms.ValidationError(f'ë³µí˜¸í™” í‚¤ ê²€ì¦ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤: {e}')

        # ì•”í˜¸í™” ìƒíƒœì—ì„œ ë³µí˜¸í™” í‚¤ë¥¼ ì…ë ¥í–ˆì„ ë•Œ
        elif was_encrypted and is_encrypted and encryption_key:
            # ì•”í˜¸í™” ìœ ì§€ ìƒíƒœì—ì„œ í‚¤ ì…ë ¥ ì²˜ë¦¬: í‚¤ê°€ ë³€ê²½ë˜ë©´ ì•ˆë¨
            try:
                input_hash = hashlib.sha256(encryption_key.encode('utf-8')).hexdigest()
                db_hash = self.instance.encryption_key_hash or ''

                if input_hash != db_hash:
                    raise forms.ValidationError(
                        'ì•”í˜¸í™”ëœ ìƒíƒœë¥¼ ìœ ì§€í•˜ë©´ì„œ í‚¤ë¥¼ ë³€ê²½í•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤. ì•”í˜¸í™”ë¥¼ í•´ì œí•˜ë ¤ë©´ ë¨¼ì € ì•”í˜¸í™” ì²´í¬ë°•ìŠ¤ë¥¼ í•´ì œí•˜ì„¸ìš”.'
                    )
            except forms.ValidationError:
                raise
            except Exception as e:
                raise forms.ValidationError('ë³µí˜¸í™” í‚¤ ê²€ì¦ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.')

        return cleaned_data

    def save(self, commit=True):
        # ê¸°ë³¸ í•„ë“œë“¤ì„ ì²˜ë¦¬í•˜ë˜ DBì—ëŠ” ì•„ì§ ì €ì¥í•˜ì§€ ì•ŠìŒ
        instance = super(ProblemForm, self).save(commit=False)

        # DBì—ì„œ í˜„ì¬ is_encrypted ìƒíƒœë¥¼ ì§ì ‘ í™•ì¸
        real_is_encrypted = False
        if instance.pk:
            try:
                with connection.cursor() as cursor:
                    cursor.execute("SELECT is_encrypted, encryption_key_hash FROM judge_problem WHERE id = %s", [instance.pk])
                    row = cursor.fetchone()
                    if row:
                        real_is_encrypted = bool(row[0])
            except Exception:
                pass

        is_encrypted = self.cleaned_data.get('is_encrypted', False)
        was_encrypted = real_is_encrypted  # ì¸ìŠ¤í„´ìŠ¤ ê°’ ëŒ€ì‹  DB ê°’ ì‚¬ìš©
        encryption_key = self.cleaned_data.get('encryption_key', '')

        try:
            if is_encrypted and not was_encrypted and encryption_key:
                # ì•”í˜¸í™” ì²˜ë¦¬
                instance._encryption_key = encryption_key

            elif was_encrypted and not is_encrypted and encryption_key:
                # ë³µí˜¸í™” ì²˜ë¦¬
                if instance.encrypted_description:
                    # ì§ì ‘ ë³µí˜¸í™” ì‹œë„
                    try:
                        # ë³µí˜¸í™” ì‹œë„
                        from judge.utils.encryption import decrypt_text
                        try:
                            decrypted_text = decrypt_text(instance.encrypted_description, encryption_key)

                            # SQL ì¸ì ì…˜ ë°©ì§€: íŒŒë¼ë¯¸í„°í™”ëœ ì¿¼ë¦¬ ì‚¬ìš©
                            if instance.pk:
                                with connection.cursor() as cursor:
                                    cursor.execute(
                                        """
                                        UPDATE judge_problem 
                                        SET description = %s, 
                                            encrypted_description = NULL, 
                                            encryption_key_hash = NULL, 
                                            is_encrypted = %s
                                        WHERE id = %s
                                        """,
                                        [decrypted_text, False, instance.pk]
                                    )

                                # ì¸ìŠ¤í„´ìŠ¤ì—ë„ ë³µí˜¸í™”ëœ ë‚´ìš© ì„¤ì •
                                instance.description = decrypted_text
                                instance.encrypted_description = None
                                instance.encryption_key_hash = None
                                instance.is_encrypted = False

                                # ì¶”ê°€ ì €ì¥ ë°©ì§€
                                if commit:
                                    # M2M ê´€ê³„ë§Œ ì €ì¥
                                    self.save_m2m()

                                    # ì €ì¥ ì™„ë£Œ í”Œë˜ê·¸
                                    instance._directly_updated = True
                                    return instance
                        except ValidationError as ve:
                            raise forms.ValidationError(f'ë³µí˜¸í™” ì‹¤íŒ¨: {ve}')

                    except Exception as e:
                        raise forms.ValidationError(f'ë³µí˜¸í™” ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤: {e}')

            # ì•”í˜¸í™” ìœ ì§€ ìƒíƒœì—ì„œ í‚¤ ì…ë ¥ ê²€ì¦ (ë²„ê·¸ ìˆ˜ì •)
            elif was_encrypted and is_encrypted and encryption_key:
                # clean ë©”ì„œë“œì—ì„œ ì´ë¯¸ ê²€ì¦ë¨ - ì¶”ê°€ ì¡°ì¹˜ í•„ìš” ì—†ìŒ
                pass
        except Exception as e:
            raise

        # ì¼ë°˜ ì €ì¥ (ë³µí˜¸í™”ê°€ ì§ì ‘ ì—…ë°ì´íŠ¸ë˜ì§€ ì•Šì€ ê²½ìš°)
        if commit and not hasattr(instance, '_directly_updated'):
            instance.save()
            self.save_m2m()

        return instance

