        min_length=4,
        max_length=50,
        help_text=_('ì•”í˜¸í™” í‚¤ëŠ” 4-50ì ì‚¬ì´ì—¬ì•¼ í•©ë‹ˆë‹¤.')
    )

    def __init__(self, *args, **kwargs):
        super(ProblemForm, self).__init__(*args, **kwargs)
        self.fields['authors'].widget.can_add_related = False
        self.fields['testers'].widget.can_add_related = False
        self.fields['change_message'].widget.attrs.update({
            'placeholder': gettext('Describe the changes you made (optional)'),
        })

        # ìƒˆ ë¬¸ì œ ìƒì„± ì‹œ is_publicì„ False(ë¹„ê³µê°œ)ë¡œ ì„¤ì •
        if not self.instance.pk:  # ìƒˆ ë¬¸ì œì¸ ê²½ìš°
            self.initial['is_public'] = False

        # descriptionì„ ì„ íƒì  í•„ë“œë¡œ ì„¤ì •
        self.fields['description'].required = False

        # ì•”í˜¸í™”ëœ ë¬¸ì œì¸ ê²½ìš° 'ë³µí˜¸í™” í‚¤'ë¡œ ë¼ë²¨ ë³€ê²½
        if self.instance and self.instance.pk:
            if self.instance.is_encrypted:
                self.fields['encryption_key'].label = _('ë³µí˜¸í™” í‚¤')
                self.fields['encryption_key'].help_text = _('ì•”í˜¸í™”ëœ ë¬¸ì œë¥¼ í¸ì§‘í•˜ë ¤ë©´ ì˜¬ë°”ë¥¸ ë³µí˜¸í™” í‚¤ë¥¼ ì…ë ¥í•˜ì„¸ìš”.')
                self.fields['description'].widget.attrs['placeholder'] = '(ì•”í˜¸í™”ëœ ë‚´ìš©)'

    def clean(self):
        cleaned_data = super().clean()
        is_encrypted = cleaned_data.get('is_encrypted', False)
        encryption_key = cleaned_data.get('encryption_key', '')

        # í˜„ì¬ ì•”í˜¸í™” ìƒíƒœ
        was_encrypted = False
        if self.instance and self.instance.pk:
            # ë°ì´í„°ë² ì´ìŠ¤ì—ì„œ ì§ì ‘ ì•”í˜¸í™” ìƒíƒœ í™•ì¸
            try:
                with connection.cursor() as cursor:
                    cursor.execute("SELECT is_encrypted FROM judge_problem WHERE id = %s", [self.instance.pk])
                    row = cursor.fetchone()
                    if row:
                        was_encrypted = bool(row[0])
            except Exception as e:
                # ì˜¤ë¥˜ ë°œìƒ ì‹œ ì¸ìŠ¤í„´ìŠ¤ ê°’ ì‚¬ìš©
                was_encrypted = self.instance.is_encrypted

        # ì•”í˜¸í™” í™œì„±í™” ê²€ì¦
        if is_encrypted and not was_encrypted:
            if not encryption_key:
                raise forms.ValidationError('ì•”í˜¸í™”ê°€ í™œì„±í™”ëœ ê²½ìš° ì•”í˜¸í™” í‚¤ë¥¼ ì…ë ¥í•´ì•¼ í•©ë‹ˆë‹¤.')
            if len(encryption_key) < 4:
                raise forms.ValidationError('ì•”í˜¸í™” í‚¤ëŠ” ìµœì†Œ 4ì ì´ìƒì´ì–´ì•¼ í•©ë‹ˆë‹¤.')

            # ì•”í˜¸í™” í‚¤ ë³µì¡ì„± ê²€ì¦
            if len(encryption_key) > 50:
                raise forms.ValidationError('ì•”í˜¸í™” í‚¤ëŠ” ìµœëŒ€ 50ìê¹Œì§€ í—ˆìš©ë©ë‹ˆë‹¤.')

        # ì´ë¯¸ ì•”í˜¸í™”ëœ ë¬¸ì œë¥¼ ì•”í˜¸í™” í•´ì œí•˜ë ¤ëŠ” ê²½ìš°
        elif was_encrypted and not is_encrypted:
            if not encryption_key:
                raise forms.ValidationError('ì•”í˜¸í™”ëœ ë¬¸ì œë¥¼ ë³µí˜¸í™”í•˜ë ¤ë©´ ë³µí˜¸í™” í‚¤ë¥¼ ì…ë ¥í•´ì•¼ í•©ë‹ˆë‹¤.')

            # í‚¤ í•´ì‹œ ê²€ì¦
            try:
                input_hash = hashlib.sha256(encryption_key.encode('utf-8')).hexdigest()
                db_hash = self.instance.encryption_key_hash or ''

                if not db_hash or input_hash != db_hash:
                    raise forms.ValidationError('ì˜ëª»ëœ ë³µí˜¸í™” í‚¤ì…ë‹ˆë‹¤. ì •í™•í•œ ë³µí˜¸í™” í‚¤ë¥¼ ì…ë ¥í•˜ì„¸ìš”.')
            except forms.ValidationError:
                raise
            except Exception as e:
                raise forms.ValidationError(f'ë³µí˜¸í™” í‚¤ ê²€ì¦ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤: {e}')

        # ì•”í˜¸í™” ìƒíƒœì—ì„œ ë³µí˜¸í™” í‚¤ë¥¼ ì…ë ¥í–ˆì„ ë•Œ
        elif was_encrypted and is_encrypted and encryption_key:
            # ì•”í˜¸í™” ìœ ì§€ ìƒíƒœì—ì„œ í‚¤ ì…ë ¥ ì²˜ë¦¬: í‚¤ê°€ ë³€ê²½ë˜ë©´ ì•ˆë¨
            try:
                input_hash = hashlib.sha256(encryption_key.encode('utf-8')).hexdigest()
                db_hash = self.instance.encryption_key_hash or ''

                if input_hash != db_hash:
                    raise forms.ValidationError(
                        'ì•”í˜¸í™”ëœ ìƒíƒœë¥¼ ìœ ì§€í•˜ë©´ì„œ í‚¤ë¥¼ ë³€ê²½í•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤. ì•”í˜¸í™”ë¥¼ í•´ì œí•˜ë ¤ë©´ ë¨¼ì € ì•”í˜¸í™” ì²´í¬ë°•ìŠ¤ë¥¼ í•´ì œí•˜ì„¸ìš”.'
                    )
            except forms.ValidationError:
                raise
            except Exception as e:
                raise forms.ValidationError('ë³µí˜¸í™” í‚¤ ê²€ì¦ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.')

        return cleaned_data

    def save(self, commit=True):
        # ê¸°ë³¸ í•„ë“œë“¤ì„ ì²˜ë¦¬í•˜ë˜ DBì—ëŠ” ì•„ì§ ì €ì¥í•˜ì§€ ì•ŠìŒ
        instance = super(ProblemForm, self).save(commit=False)

        # DBì—ì„œ í˜„ì¬ is_encrypted ìƒíƒœë¥¼ ì§ì ‘ í™•ì¸
        real_is_encrypted = False
