            // XSS ë°©ì§€: ì…ë ¥ê°’ ê²€ì¦
            var password = $('#problem-password').val();

            // ë¹ˆ ê°’ ê²€ì¦
            if (!password) {
                alert('ë¹„ë°€ë²ˆí˜¸ë¥¼ ì…ë ¥í•´ì£¼ì„¸ìš”.');
                return;
            }

            // ê¸¸ì´ ê²€ì¦ - í´ë¼ì´ì–¸íŠ¸ ì¸¡ì—ì„œë„ ì² ì €íˆ ê²€ì¦
            if (password.length < 4) {
                alert('ë¹„ë°€ë²ˆí˜¸ëŠ” ìµœì†Œ 4ì ì´ìƒì´ì–´ì•¼ í•©ë‹ˆë‹¤.');
                return;
            }

            if (password.length > 50) {
                alert('ë¹„ë°€ë²ˆí˜¸ëŠ” ìµœëŒ€ 50ìê¹Œì§€ ì…ë ¥ ê°€ëŠ¥í•©ë‹ˆë‹¤.');
                return;
            }

            // ì´ìƒí•œ ë¬¸ì ê²€ì¦ ì¶”ê°€
            if (/[<>]/.test(password)) {
                alert('ë¹„ë°€ë²ˆí˜¸ì— í—ˆìš©ë˜ì§€ ì•ŠëŠ” ë¬¸ìê°€ í¬í•¨ë˜ì–´ ìˆìŠµë‹ˆë‹¤.');
                return;
            }

            var currentPath = window.location.pathname;
            if (!currentPath.endsWith('/')) {
                currentPath += '/';
            }

            // ë²„íŠ¼ ë¹„í™œì„±í™” (ì¤‘ë³µ ì œì¶œ ë°©ì§€)
            $('#check-password').prop('disabled', true).text('í™•ì¸ ì¤‘...');

            $.ajax({
                url: currentPath + 'check-password/',
                type: 'POST',
                headers: {
                    "X-CSRFToken": '{{ csrf_token }}'
                },
                data: {
                    'password': password
                },
                success: function (response) {
                    if (response.status === 'success') {
                        // ì•”í˜¸í™”ëœ ë¬¸ì œ í¼ ìˆ¨ê¸°ê¸°
                        $('.encrypted-problem').hide();

                        // ë³µí˜¸í™”ëœ ë‚´ìš© í‘œì‹œ
                        var $problemContent = $('#problem-content');
                        if ($problemContent.length === 0) {
                            $problemContent = $('<div id="problem-content"></div>');
                            $('.encrypted-problem').after($problemContent);
                        }

                        $problemContent.html(response.description);

                        // MathJaxê°€ ìˆëŠ” ê²½ìš°ì—ë§Œ ì‹¤í–‰
                        if (typeof MathJax !== 'undefined') {
                            try {
                                MathJax.typesetPromise && MathJax.typesetPromise();
                            } catch (e) {
                                console.error('MathJax error:', e);
                            }
                        }
                    } else {
                        alert(response.message || 'ì˜¬ë°”ë¥´ì§€ ì•Šì€ ë¹„ë°€ë²ˆí˜¸ì…ë‹ˆë‹¤.');
                    }
                },
                error: function (xhr, status, error) {
                    var errorMsg = 'ì„œë²„ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤. ë‹¤ì‹œ ì‹œë„í•´ì£¼ì„¸ìš”.';
                    try {
                        var response = JSON.parse(xhr.responseText);
                        if (response.message) {
                            errorMsg = response.message;
                        }
                    } catch (e) { }
                    alert(errorMsg);
                },
                complete: function () {
                    // ë²„íŠ¼ ì¬í™œì„±í™”
                    $('#check-password').prop('disabled', false).text('í™•ì¸');
                    // ë¹„ë°€ë²ˆí˜¸ í•„ë“œ ì´ˆê¸°í™” (ë³´ì•ˆ)
                    $('#problem-password').val('');
                }
            });
        });

        // ì—”í„°í‚¤ ì²˜ë¦¬
        $('#problem-password').keypress(function (e) {
            if (e.which == 13) {
                e.preventDefault();
                $('#check-password').click();
            }
        });
    });
</script>
{% endif %}
