{% load wpadmin_tags wpadmin_menu_tags %}{% load static %}
<!DOCTYPE html>
<html lang="{{ LANGUAGE_CODE|default:" en-us" }}"{% if LANGUAGE_BIDI %} dir="rtl" {% endif %}>

<head>
	{# WP Admin start #}
	<meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />
	<meta name="viewport" content="width=device-width, initial-scale=1.0" />
	{# WP Admin end #}
	<title>{% block title %}{% endblock %}</title>
	<link rel="stylesheet" type="text/css" href="{% block stylesheet %}{% static "admin/css/base.css" %}{% endblock %}" />
	{% block extrastyle %}{% endblock %}
	{% if LANGUAGE_BIDI %}
	<link rel="stylesheet" type="text/css" href="{% block stylesheet_rtl %}{% static "admin/css/rtl.css" %}{% endblock %}" />
	{% endif %}
	{# WP Admin start #}
	{% if INLINE_FONTAWESOME %}
	<link rel="stylesheet" type="text/css" href="{% static "libs/fontawesome/font-awesome.css" %}" />
	{% elif FONTAWESOME_CSS %}
	<link rel="stylesheet" type="text/css" href="{{ FONTAWESOME_CSS }}" />
	{% else %}
	<link rel="stylesheet" type="text/css" href="{% static "wpadmin/css/font-awesome.min.css" %}" />
	{% endif %}
	{% with WPADMIN_DEV=0 %}{% if WPADMIN_DEV %}
	<link rel="stylesheet/less" type="text/css" href="{% static "wpadmin/less/wpadmin.less" %}" />
	<link rel="stylesheet/less" type="text/css" href="{% static "wpadmin/less/themes/default/theme.less" %}" />
	<script type="text/javascript">less = { env: "development" };</script>
	<script type="text/javascript" src="{% static "wpadmin/js/lib/less.min.js" %}"></script>
	{% else %}
	<link rel="stylesheet" type="text/css" href="{% static "wpadmin/css/wpadmin.css" %}" />
	<link rel="stylesheet" type="text/css" href="{% static "wpadmin/css/themes/default.css" %}" />
	{% endif %}{% endwith %}
	{# WP Admin end #}
	<script
		type="text/javascript">window.__admin_media_prefix__ = "{% filter escapejs %}{% static "admin/" %}{% endfilter %}";</script>
	<script
		type="text/javascript">window.__admin_utc_offset__ = "{% filter escapejs %}{% now "Z" %}{% endfilter %}";</script>
	{% block extrahead %}{% endblock %}
	{% wpadmin_render_custom_style %}
	{% block wpadmin_extrastyle %}{% endblock %}
	{% block wpadmin_extrahead %}{% endblock %}
	{# WP Admin start #}
	<script type="text/javascript">
		// needed for setting cookie path
		window.__admin_url = '{% url 'admin:index' %}';
		// cookie name
		window.__wpadmin_cookie_name = 'djangoWpAdminSettings';

		// we need jQuery in default namespace (not django.jQuery) because we use some external jQuery plugins
		if (typeof jQuery == 'undefined') {
			// we can get jQuery object from django object but there are some admin templates where jQuery is not loaded
			if (typeof django == 'undefined' || typeof django.jQuery == 'undefined') {
				// so lets load our jQuery synchronously
				document.write(decodeURI("%3Cscript src='{% static "wpadmin/js/lib/jquery.min.js" %}' type='text/javascript'%3E%3C/script%3E"));
			} else {
				// lest copy jQuery from django object to default namespace
				window.jQuery = django.jQuery;
			}
		}
	</script>
	<script type="text/javascript" src="{% static "wpadmin/js/lib/jquery.cookie.min.js" %}"></script>
	<script type="text/javascript" src="{% static "wpadmin/js/menus.js" %}"></script>
	<script type="text/javascript" src="{% static "wpadmin/js/content.js" %}"></script>
	<link rel="stylesheet" type="text/css" href="{% static "wpadmin/css/wpadmin.site.css" %}" />
	{# WP Admin end #}
	{% block blockbots %}
	<meta name="robots" content="NONE,NOARCHIVE" />{% endblock %}
	<style>
		:root {
			background-color: #F2F2F2;
			--border-color: rgba(0, 0, 0, 0.07);
			--border-secondary: #e5e5e5;
			--checkbox-color: #1D7FB4;
		}

		/* hover, focus, active 상태에서 효과 제거 */
		input:hover,
		input:focus,
		input:active,
		select:hover,
		select:focus,
		select:active,
		textarea:hover,
		textarea:focus,
		textarea:active {
			outline: none !important;
			box-shadow: none !important;
			background: inherit;
			transition: none !important;
		}

		#wrapper {
			margin-top: 48px;
		}

		#adminbarwrap #adminbar {
			background-color: #1C3B60;
			box-shadow: 0px 7px 29px 0px rgba(100, 100, 111, 0.1);
		}

		#content {
			height: 100%;
			margin: 0;
			padding: 10px 50px;
		}

		.breadcrumbs {
			background-color: #FFFFFF !important;
			border: 1px solid var(--border-secondary);
			color: #5C95D9 !important;
			border-radius: 10px;
			padding: 15px 20px;
		}

		.breadcrumbs a {
			margin: 10px;
			color: #8391A1 !important;
		}

		.breadcrumbs a:hover {
			color: #5C95D9 !important;
		}

		#content .module {
			background-color: #FFFFFF;
			border-radius: 10px;
			border: 1px solid var(--border-secondary);
			overflow: hidden;
		}

		#content .module h2,
		#content .module caption,
		#content .inline-group h2 {
			border-top-left-radius: 10px;
			border-top-right-radius: 10px;
			border: none;
			background-color: #F8F8F8;
		}

		#content .form-row.errors {
			border-top: 1px solid #ccc;
		}

		#content .form-row {
			border: none;
			border-top: 1px solid #ccc;
			padding: 10px 20px;
			overflow: auto;
		}

		#content textarea,
		#content input[type="text"],
		#content input[type="password"],
		#content input[type="email"],
		#content input[type="number"],
		#content input[type="search"],
		#content input[type="tel"],
		#content input[type="url"],
		#content input[type="file"],
		#content select {
			border-radius: 7px;
		}

		#content .form-row>div {
			margin: 0;
		}

		#content label {
			width: auto;
			font-weight: bold;
			color: black;
		}

		.select2-container--default.select2-container--focus .select2-selection--multiple {
			border: 1px solid var(--border-secondary);
			box-shadow: 0 1px 2px rgba(0, 0, 0, 0.07) inset
		}

		.select2-container--default .select2-selection--multiple {
