                        {% endfor %}
                    </ul>
                    <span class="rssatom">
                        <a href="{{ url('problem_rss') }}"><span><i class="fa fa-rss"></i></span> RSS</a>
                        /
                        <a href="{{ url('problem_atom') }}">Atom</a>
                    </span>
                </div>
            </div>
        </div> -->
