{% extends "base.html" %}

{% block media %}
    {% include "timezone/media-css.html" %}
    {{ form.media.css }}
    <style>
        #page-container {
            background: #FFF !important;
        }
        footer {
            background: #FFF !important;
        }
        .title-box {
            display: none;
        }
        #content {
            margin: 50px auto 0;
            width: 1040px;
        }
        .auth-flow-form {
            max-width: 100%;
            background-color: white;
            border-radius: 10px;
            text-align: center;
        }

        .auth-flow-form h2 {
            margin-bottom: 20px;
            font-size: 24px;
            text-align: center;
            font-weight: 400;
        }

        /* #edit-form {
            border: unset;
            background: unset;
            width: 30%;
        } */

        .form-area {
            width: 354px;
            display: flex;
            flex-direction: column;
            background-color: white;
            border: none;
            gap: 10px;
            margin: 0 auto;
        }
        /* @media (max-width: 1200px) {
            .form-area {
                width: 350px;
            }
        } */
        .fullwidth {
            border-radius: 8px;
            overflow: visible !important;
        }

        .form-area input,
        .form-area input[type="text"],
        .form-area input[type="password"],
        .form-area input[type=email]
        { 
            padding: 10px 10px 10px 16px;
            font-size: 14px;
            border: 1px solid #ddd;
            border-radius: 8px;
            width: 100%;
            height: 46px;
            box-sizing: border-box;
            font-family: "Pretendard";
        }


        /* 이메일 입력 전체 영역 스타일 */
        .email-input-wrapper {
            display: flex;
            align-items: center;
            width: 100%;
        }
        .email-input-wrapper-error {
            display: flex;
            align-items: center;
            width: 100%;
        }
        /* 로컬 이메일 입력 필드 스타일 정의 */
        input[type=text].email_local {
            color: #000;
            background: #fff;
            border: 1px solid var(--border-color);
            border-right: none;  /* 오른쪽 테두리 제거 */
            border-radius: 8px 0 0 8px;  /* 왼쪽만 둥글게 */
            padding: 10px 10px 10px 16px;
            font-size: 14px;
            box-sizing: border-box;
            height: 46px;
            width: 72%;  /* 이메일 로컬 부분의 너비 조정 */
            font-family: "Pretendard";
        }
        /* 도메인 부분 스타일 정의 */
        span.email_domain {
        background: #f1f1f1;
        border: 1px solid var(--border-color);
        border-left: none;  /* 왼쪽 테두리 제거 */
        border-radius: 0 8px 8px 0;  /* 오른쪽만 둥글게 */
        padding: 10px;
        height: 46px;  /* input 높이와 맞춤 */
        display: flex;
        align-items: center;
        box-sizing: border-box;
        width: 28%;  /* 도메인 부분의 너비 조정 */
        }

        .select2-hidden-accessible {
            border: 1px solid var(--border-color)!important;
            clip: rect(0 0 0 0) !important;
            -webkit-clip-path: inset(50%) !important;
            clip-path: inset(50%) !important;
            height: 1px !important;
            overflow: hidden !important;
            padding: 10px !important;
            position: absolute !important;
            width: 1px !important;
            white-space: nowrap !important; 
        }

        .form-area button,
        .form-area .button {
            color: #FFF;
            background: none;
            background-color: #5C95D9;
            border-radius: 8px;
            border: 1px solid #5C95D9;
            font-size: 14px;
            font-weight: bold;
            cursor: pointer;
            line-height: 1.5;
            height: 40px;
            font-family: "Pretendard";
            transition: background-color 0.3s ease, color 0.3s ease;        
        }

        .form-area button:hover,
        .form-area a.button:hover {
            background-color: #5288C8;
            color: #FAFAFA;
        }

        .fullwidth {
            display: block;

