            {{ form.email_domain }}
            <span class="email_domain">@jbnu.ac.kr</span>
        </span>

        {% if form.email_local.errors %}
            <div class="form-field-error">{{ form.email_local.errors }}</div>
        {% endif %}

        <button type="submit" class="button">{{ _('이메일 변경') }}</button>
    </form>
</div>
{% endblock %}

