{% extends "base.html" %}

{% block media %}
<style>
    footer {
        background: #FFF !important;
    }
    #page-container {
        display: flex;
        background: #fff !important;
    }
    .title-div {
        display: none;
    }
    #content {
        display: flex;
        justify-content: center;
        align-items: center;
        min-height: 90vh !important;
        margin-top: 0px !important;
        margin: auto;
        width: 1040px;
    }
    .auth-flow-form {
        max-width: 100%;
        margin: 50px auto;
        padding: 20px;
        background-color: white;
        border-radius: 10px;
        text-align: center;
    }

    .auth-flow-form h2 {
        margin-bottom: 20px;
        font-size: 24px;
        font-weight: 400;
    }

    .form-area {
        width: 354px;
        display: flex;
        flex-direction: column;
        background-color: white;
        border: none;
        gap: 10px;
        margin: 0 auto;
    }

    .form-area .button {
        height: 40px;
        border-radius: 8px;
        font-size: 14px;
        font-weight: bold;
        cursor: pointer;
        background: none;
        transition: background-color 0.3s ease, color 0.3s ease;
    }

    .form-area button[type="submit"] {
        color: #FFF;
        background-color: #5C95D9;
        border: 1px solid #5C95D9;
    }

    .form-area a.button {
        color: #5C95D9 !important;
        background-color: #FFF;
        border: 1px solid #D9D9D9;
        display: flex;
        flex-direction: column;
        justify-content: center;
        padding: 0;
    }

    .form-area button:hover {
        background-color: #5288C8;
        color: #FAFAFA;
    }

    .form-area input[type="text"],
    .form-area input[type="password"],
    .form-area input[type="email"] {
        height: 46px;
        padding: 10px 10px 10px 16px;
        border: 1px solid #ddd;
        border-radius: 8px;
        font-size: 14px;
        transition: border-color 0.15s ease-in-out 0s, box-shadow 0.15s ease-in-out 0s;
        box-sizing: border-box;
    }

    input[type="text"]:hover, input[type="password"]:hover, input[type="email"]:hover {
        box-shadow: 0 1px 1px rgba(0, 0, 0, 0.075) inset;
    }

    input[type="text"]:focus, input[type="password"]:focus, input[type="email"]:focus {
        box-shadow: none;
        border-color: #9FCDF2;
        outline: 0;
    }

    footer {
        margin-top: 30px;
    }

    .footer-content {
        text-align: center;
        color: #808080;
    }

    /* 이메일 입력 전체 영역 스타일 */
    .email-input-wrapper {
        display: flex;
        align-items: center;
        width: 100%;
    }

    /* 로컬 이메일 입력 필드 스타일 정의 */
    input[type=text].email_local {
        color: #000;
        background: #fff;
        border: 1px solid #ddd;
        border-right: none;
        border-radius: 8px 0 0 8px;
        padding: 10px 10px 10px 16px;
        font-size: 14px;
        box-sizing: border-box;
        height: 46px;
        width: 72%;
        font-family: "Pretendard";
    }
</style>
{% endblock %}

{% block body %}
<div class="auth-flow-form">
    <h2>활성화 메일 재전송</h2>
    <form action="" method="post" class="form-area">
        {% csrf_token %}
        {% if form.errors %}
            <div id="form-errors">
                {% for field, errors in form.errors.items() %}
                    {% for error in errors %}
                        <p class="error">{{ error }}</p>
                    {% endfor %}
                {% endfor %}
            </div>
        {% endif %}

        {{ form.username }}
        {{ form.password }}

        <button type="submit" class="button">{{ _('활성화 메일 재전송') }}</button>
    </form>
</div>
{% endblock %}

