    width: 75%;
    margin: 0 auto;
    position: relative;
}

nav {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    z-index: 500;
}
nav ul {
    margin: 0 0 0 -5px;
    padding: 0;
    display: block;
}
nav ul li {
    display: inline-block;
    color: var(--font-black);
    text-transform: uppercase;
    position: relative;
}
