    79→        <script>
    80→            // 네비게이션바 active 상태 처리
    81→            document.addEventListener('DOMContentLoaded', function() {
    82→                const currentPath = window.location.pathname;
    83→                const navLinks = document.querySelectorAll('#nav-list > li > a');
    84→                
    85→                navLinks.forEach(link => {
    86→                    const href = link.getAttribute('href');
    87→                    if (href && currentPath.includes(href) && href !== '/') {
    88→                        link.classList.add('active');
    89→                    } else if (href === '/' && currentPath === '/') {
    90→                        link.classList.add('active');
    91→                    }
    92→                });
    93→            });
    94→        </script>
