   123→    {% if request.in_contest %}
   124→    <script>
   125→        $(function () {
   126→            count_down($("#contest-time-remaining"));
   127→        
   128→            var selected = null,
   129→                x_pos = 0, y_pos = 0,
   130→                x_elem = 0, y_elem = 0;
   131→        
   132→            $('#contest-info').mousedown(function () {
   133→                selected = $(this);
   134→                x_elem = x_pos - selected.offset().left;
   135→                y_elem = y_pos - (selected.offset().top - $(window).scrollTop());
   136→                return false;
   137→            });
   138→        
   139→            if (localStorage.getItem("contest_timer_pos")) {
   140→                data = localStorage.getItem("contest_timer_pos").split(":");
   141→                $("#contest-info").css({
   142→                    left: data[0],
   143→                    top: data[1]
   144→                });
   145→            }
   146→        
   147→            $("#contest-info").show();
   148→        
   149→            $(document).mousemove(function (e) {
   150→                x_pos = e.screenX;
   151→                y_pos = e.screenY;
   152→                x_pos = Math.max(Math.min(x_pos, window.innerWidth), 0);
   153→                y_pos = Math.max(Math.min(y_pos, window.innerHeight), 0);
   154→        
   155→                if (selected !== null) {
   156→                    var elementWidth = selected.outerWidth();
   157→                    var elementHeight = selected.outerHeight();
   158→                    var padding = 10;  // 10px 여백
   159→        
   160→                    // 화면 안쪽 10px 만큼 제한
   161→                    var left_px = Math.max(padding, Math.min((x_pos - x_elem), window.innerWidth - elementWidth - padding)) + 'px';
   162→                    var top_px = Math.max(padding, Math.min((y_pos - y_elem), window.innerHeight - elementHeight - padding)) + 'px';
   163→        
   164→                    localStorage.setItem("contest_timer_pos", left_px + ":" + top_px);
   165→        
   166→                    selected.css({
   167→                        left: left_px,
   168→                        top: top_px
   169→                    });
   170→                }
   171→            });
   172→        
   173→            $(document).mouseup(function () {
   174→                selected = null;
   175→            });
   176→        });
   177→    </script>
   178→    {% endif %}
