   380→            <!-- 제출 결과 요약 카드 -->
   381→            <div class="status-card {% if submission.result == 'AC' %}card-success{% elif submission.result in ['WA', 'TLE', 'MLE', 'RTE', 'OLE', 'IR'] %}card-failure{% else %}card-other{% endif %}">
   382→                <div class="card-header">
   383→                    <div style="display: flex; justify-content: space-between; align-items: center;">
