                                                {% endif %}
                                                {% if case.extended_feedback %}
                                                    <td colspan="5" class="case-ext-feedback">
                                                        <div class="case-info">
                                                            <strong>{{ _('Judge feedback') }}</strong>
                                                            <pre class="case-output">{{ case.extended_feedback|linebreaksbr }}</pre>
                                                        </div>
                                                    </td>
                                                {% endif %}
                                            </tr>
                                        {% endif %}
                                        {% endfor %}
                                    {% endfor %}
                                </tbody>
                            </table>
                        {% endif %}
                    {% endif %}
                </div>
            </div>
        {% endif %}
    {% else %}
        {% include "submission/internal-error-message.html" %}
    {% endif %}

