    .testcase-row {
        position: relative;
        transition: background-color 0.2s ease;
    }

    .testcase-row:hover {
        background-color: #f8f9fa;
    }

    .testcase-row {
        background-color: white;
    }

    .status-ac, .AC {
        color: #4CAF50;
        font-weight: 600;
    }

    .status-wa, .WA {
        color: #F44336;
        font-weight: 600;
    }

    .status-tle, .TLE,
    .status-rte, .RTE,
    .status-mle, .MLE {
        color: #FFB300;
        font-weight: 600;
    }

    .status-ole, .OLE,
    .status-ir, .IR {
        color: #546E7A;
        font-weight: 600;
    }

    .status-ab, .AB {
        color: #6c757d;
        font-weight: 600;
    }

    .case-number {
        font-weight: 600;
        color: #495057;
    }

    .execution-time {
        color: #6c757d;
