{% block content_media %}
<style>
    .title-div {
        margin-top: 24px;
    }
    .title-area {
        margin-bottom: 0;
        margin-top: 0;
    }
    .title-div .title {
        margin-top: 0;
    }
    .status-card {
        background: white;
        border-radius: 12px;
        box-shadow: 0 4px 6px rgba(0, 0, 0, 0.05);
        margin-bottom: 20px;
        overflow: hidden;
        padding: 20px 30px;
    }

    .status-card.card-success {
        border-left: 5px solid #4CAF50;
    }

    .status-card.card-failure {
        border-left: 5px solid #F44336;
    }

    .status-card.card-other {
        border-left: 5px solid #FFB300;
    }

    .card-header {
        padding-bottom: 10px;
        border-bottom: 1px solid var(--border-color);
    }

    .card-title {
        font-size: 18px;
        font-weight: 600;
        color: var(--font-basic-black);
        margin: 0;
        font-family: Pretendard, sans-serif;
    }

    .result-badge {
        display: inline-flex;
        align-items: center;
        gap: 6px;
        padding: 8px 16px;
        border-radius: 8px;
        font-size: 15px;
        font-weight: 600;
        white-space: nowrap;
    }

    .badge-success {
        background: #E6F4EA;
        color: #1E7F3E;
        border: 1px solid #A8DAB5;
    }

    .badge-failure {
        background: #FDECEC;
        color: #B42318;
        border: 1px solid #F5C2C7;
    }

    .badge-other {
        background: #FFF4E5;
        color: #856404;
        border: 1px solid #FFE0B2;
    }

    .result-summary {
        padding: 12px 16px;
        margin-bottom: 16px;
        border-radius: 8px;
        font-size: 14px;
        font-weight: 500;
        display: flex;
        align-items: center;
        gap: 8px;
    }

    .result-summary.success {
        background: #E6F4EA;
        color: #1E7F3E;
        border: 1px solid #A8DAB5;
    }

    .result-summary.failure {
        background: #FDECEC;
        color: #B42318;
        border: 1px solid #F5C2C7;
    }

    .card-content {
        padding-top: 10px;
    }

    .summary-grid {
        display: flex;
        flex-direction: column;
        gap: 8px;
        margin-bottom: 12px;
    }

    .summary-item {
        display: flex;
        align-items: center;
        gap: 8px;
    }

    .summary-label {
        color: var(--font-basic-black);
        font-size: 14px;
    }

    .summary-value {
        font-weight: 500;
        color: var(--font-black);
    }

    .summary-value.passed {
        color: #4CAF50;
    }

    .summary-value.failed {
        color: #F44336;
    }

    .summary-value.score {
        font-size: 14px;
        font-weight: 600;
    }

    .warning-text {
        background: rgba(28, 59, 96, 0.06);
        border: 1px solid var(--border-color);
        border-radius: 8px;
        padding: 16px;
        color: #856404;
        font-family: 'Courier New', monospace;
        font-size: 14px;
        line-height: 1.5;
        white-space: pre-wrap;
    }

    .action-buttons {
        display: flex;
        gap: 12px;
        justify-content: flex-end;
    }

    .action-btn {
        padding: 6px 16px;
        border-radius: 6px;
        border: none;
        font-size: 14px;
        font-weight: 400;
        cursor: pointer;
        text-decoration: none;
        display: inline-flex;
        align-items: center;
        gap: 6px;
        transition: all 0.2s ease;
        background: #edf3fb;
        color: #5285c4;
    }

    .action-btn:hover {
        background: #e3eaf6;
        color: #6291d0;
        text-decoration: none;
    }

    .testcase-table {
        width: 100%;
        border-collapse: separate;
        border-spacing: 0;
        background: white;
        border-top-left-radius: 8px;
        border-top-right-radius: 8px;
        border: 1px solid var(--border-color);
    }

    .testcase-table th {
        background: #f8f9fa;
        padding: 6px 16px;
        text-align: center;
        font-weight: 600;
        color: #495057;
        border-bottom: 1px solid var(--border-color);
        font-size: 14px;
    }

    .testcase-table th:first-child {
        border-top-left-radius: 8px;
    }

    .testcase-table th:last-child {
        border-top-right-radius: 8px;
    }

