    59→    {% endcompress %}
    60→    <link rel="canonical"
    61→          href="{{ DMOJ_SCHEME }}://{{ DMOJ_CANONICAL|default(site.domain) }}{{ request.get_full_path() }}">
    62→    {% if request.user.is_impersonate %}
