    78→        {% endif %}
    79→        <script>
    80→            // 네비게이션바 active 상태 처리
    81→            document.addEventListener('DOMContentLoaded', function() {
    82→                const currentPath = window.location.pathname;
    83→                const navLinks = document.querySelectorAll('#nav-list > li > a');
    84→                
    85→                navLinks.forEach(link => {
    86→                    const href = link.getAttribute('href');
    87→                    if (href && currentPath.includes(href) && href !== '/') {
    88→                        link.classList.add('active');
    89→                    } else if (href === '/' && currentPath === '/') {
    90→                        link.classList.add('active');
    91→                    }
    92→                });
    93→            });
    94→        </script>
    95→        <script src="{{ static('libs/jquery-cookie.js') }}"></script>
    96→        <script src="{{ static('libs/jquery-taphold.js') }}"></script>
    97→        <script src="{{ static('libs/jquery.unveil.js') }}"></script>
    98→        <script src="{{ static('libs/moment.js') }}"></script>
    99→        <script src="{{ static('libs/select2/select2.js') }}"></script>
   100→        {% include "extra_js.html" %}
   101→        <script src="{{ static('common.js') }}"></script>
   102→        <script>
   103→            moment.locale('{{ LANGUAGE_CODE }}');
   104→            set_date_locale('{{ LANGUAGE_CODE }}');
   105→            $(function () {
   106→                $('img.unveil').unveil(200);
   107→            });
   108→        </script>
   109→         <!-- JavaScript for toggling menu -->
   110→        <script>
   111→            function toggleMenu() {
   112→                var navList = document.getElementById('nav-list');
   113→                if (navList.classList.contains('show')) {
   114→                    navList.classList.remove('show');
   115→                } else {
   116→                    navList.classList.add('show');
   117→                }
   118→            }
