   253→                $('.filter-select2').not(sortContent).hide(); // 다른 모든 드롭다운 숨기기
   254→                $('.filter-btn').not(this).removeClass('show'); // 다른 모든 버튼 상태 리셋
   255→
   256→                var arrowIcon = $(this).find('.arrow');
