   271→            register_notify('ticket', {
   272→                $checkbox: $('#notification'),
   273→                change: function (enabled) {
   274→                    if (!enabled)
   275→                        for (key in localStorage)
   276→                            if (key.startsWith('ticket:open:'))
   277→                                delete localStorage[key];
   278→                }
   279→            });
   280→
   281→            function main_list_notify(id) {
   282→                key = 'ticket:open:' + id;
   283→                return !(key in localStorage) || localStorage[key] == '0';
   284→            }
   285→
   286→            var $tbody = $('#ticket-list').find('tbody');
   287→
   288→            function new_ticket(ticket) {
   289→                console.log('Fetching data for: ' + ticket.id);
   290→                $.ajax({
   291→                    url: '{{ url('ticket_ajax') }}',
   292→                    data: {id: ticket.id},
   293→                    success: function (data) {
   294→                        console.log('Got data for: ' + ticket.id);
   295→                        console.log(data);
   296→                        $tbody.prepend($(data.row));
   297→                        notify('ticket', data.notification.title, {
   298→                            body: data.notification.body
   299→                        });
   300→                    },
   301→                    error: function (data) {
   302→                        if (data.status === 403)
   303→                            console.log('No right to see: ' + ticket.id);
   304→                        else {
   305→                            console.log('Could not load ticket:');
   306→                            console.log(data.responseText);
   307→                        }
   308→                    }
   309→                });
   310→            }
   311→
   312→            function ticket_status(ticket) {
   313→                if (!main_list_notify(ticket.id)) return;
   314→
   315→                var $row = $('#ticket-' + ticket.id);
   316→                console.log('Ticket status change: ' + ticket.id);
   317→                if ($row.length) {
   318→                    var $status = $row.find('td').first().find('i');
   319→                    if (ticket.open) {
   320→                        $status.removeClass('fa-check-circle-o').addClass('fa-exclamation-circle');
   321→                        notify('ticket', '{{ _('Reopened: ') }}' + ticket.title);
   322→                    } else {
   323→                        $status.removeClass('fa-exclamation-circle').addClass('fa-check-circle-o');
   324→                        notify('ticket', '{{ _('Closed: ') }}' + ticket.title);
   325→                    }
   326→                }
   327→            }
   328→
   329→            window.load_dynamic_update = function (last_msg) {
   330→                var $assignees = $(filter_assignee_ids);
   331→
   332→                return new EventReceiver(
   333→                    "{{ EVENT_DAEMON_LOCATION }}", "{{ EVENT_DAEMON_POLL_LOCATION }}",
   334→                    ['tickets'], last_msg, function (message) {
   335→                        console.log(message);
   336→                        if (filter_own_id != null && message.user != filter_own_id &&
   337→                            !~message.assignees.indexOf(filter_own_id))
   338→                            return;
   339→                        if (filter_user_ids.length && !~filter_user_ids.indexOf(message.user))
   340→                            return;
   341→                        if ($assignees.length && !$assignees.filter(message.assignees).length)
   342→                            return;
   343→                        switch (message.type) {
   344→                            case 'new-ticket':
   345→                                new_ticket(message);
   346→                                break;
   347→                            case 'ticket-status':
   348→                                ticket_status(message);
   349→                                break;
   350→                        }
   351→                    }
   352→                );
   353→            };
