		#content input[type="url"],
		#content input[type="file"],
		#content select {
			border-radius: 7px;
		}

		#content .form-row>div {
			margin: 0;
		}

		#content label {
			width: auto;
			font-weight: bold;
			color: black;
		}

		.select2-container--default.select2-container--focus .select2-selection--multiple {
			border: 1px solid var(--border-secondary);
			box-shadow: 0 1px 2px rgba(0, 0, 0, 0.07) inset
		}

		.select2-container--default .select2-selection--multiple {
			border: 1px solid var(--border-secondary);
			box-shadow: 0 1px 2px rgba(0, 0, 0, 0.07) inset
		}

		#adminbar>.wp-menu-top.wp-menu-open {
			display: none;
		}

		.calendarbox .calendarnav-previous,
		.calendarbox .calendarnav-next {
			padding: 0;
			padding-top: 0px;
			padding-right: 0px;
			padding-bottom: 0px;
			padding-left: 0px;
			text-align: center;
			width: 19px;
			height: 15px;
			line-height: 19px;
			font-size: 18px;
		}
		#content #content-main #bottombar{
			background-color: #F2F2F2;
		}
		#content #content-main #bottombar button{
			border-radius :8px;
			background-color: #4C79B6;
			opacity: 1;
		}
		#content #content-main #bottombar a{
			border-radius :8px;
			background-color: #4C79B6 !important;
			opacity: 1;
		}
		#content #content-main #bottombar .deletelink{
			background-color : #E05252 !important;
			color:white !important;
		}
		#content #content-main .module .field-date .datetime{
			position: relative;
			vertical-align: middle;
			align-items: center;
		}
		#content #content-main .module .field-date .datetime input{
			padding-right: 80px;
			margin-right: -60px;
		}

		#content #content-main .module .field-date .datetime span{
			text-align: left;
			direction: ltr;
			right:0;
		}
		#content{
			color: #333333;
			letter-spacing: -0.025em;
		}
		#content #content-main .module .form-row .help{
			font-size:10px !important;
		}

		/* 날짜/시간 필드의 br 태그 제거 */
		.datetime br {
			display: none !important;
		}

		/* 날짜/시간 입력 필드 레이아웃 개선 */
		.datetime input {
			display: inline-block !important;
			margin-right: 5px !important;
		}

		#content #content-main .module .field-is_encrypted #id_is_encrypted{
			accent-color: var(--checkbox-color);
		}
		#content #content-main .module .field-is_public #id_is_public{
			accent-color: var(--checkbox-color);
		}
		#content #content-main .module .field-allowed_languages .related-widget-wrapper{
			accent-color: var(--checkbox-color);
		}
		#content #content-main .module .field-date .datetimeshortcuts > a,
		#content #content-main .module .field-date .datetimeshortcuts > a > span{
			filter: invert(60%) sepia(0%) saturate(1%) hue-rotate(163deg) brightness(95%) contrast(86%);
		}
		#content #content-main #language_limits-group .module tbody .add-row a,
		#content #content-main #cases-group .module tbody .add-row a,
		#content #content-main #solution-group .module .add-row a,
		#content #content-main #clarifications-group .module .add-row a{
			border-radius :8px;
			padding: 5px 5px 5px 5px;
			color: black;
			font-size:13px;
			display:inline-block;
			margin-top:3px;
		}
		#content #content-main #language_limits-group .module tbody .add-row td,
		#content #content-main #cases-group .module tbody .add-row td,
		#content #content-main #solution-group .module .add-row td,
		#content #content-main #clarifications-group .module .add-row td{
			height:30px;
		}
		#content #content-main #language_limits-group .module tbody .add-row a::before,
		#content #content-main #cases-group .module tbody .add-row a::before,
		#content #content-main #solution-group .module .add-row a::before,
		#content #content-main #clarifications-group .module .add-row a::before{
			color: #3A6FD9;
		}
		#content #content-main .module .field-allowed_languages #allowed_languages_all > label{
			padding-left:31px;
		}

		/* 문제 관리 페이지 디자인 일부 수정 */
		#content .object-tools li a, #content .list-tools li a {
			border: 0.5px solid var(--border-color);
			background-color: #E6E7EA;
			color: #2E5DA8;
			border-radius: 8px;
			padding: 4px 15px;
		}
		#content .object-tools li a:hover, #content .list-tools li a:hover {
			background-color: #E6E7EA;
			color: #2E5DA8;
		}
		#content .datetimeshortcuts {
			margin-right: 30px;
		}
		.aligned label + p, .aligned label + div.help, .aligned label + div.readonly {
			margin-left: 0;
		}
		div.original-checkboxes ul {
			columns: 10em 4 !important;
		}
	</style>
</head>
{% load i18n %}

{% wpadmin_are_breadcrumbs_enabled %}{% wpadmin_is_left_menu_enabled %}

<body
	class="{% if is_popup %}popup {% endif %}{% if not wpadmin_are_breadcrumbs_enabled %}wp-no-breadcrumbs {% endif %}{% if wpadmin_is_left_menu_enabled %}wp-with-left-menu {% endif %}{% block bodyclass %}{% endblock %} wp-pinned">

	<!-- Wrapper -->
	<div id="wrapper">
		{% if not is_popup %}
		{% block adminmenu %}
		{% wpadmin_render_left_menu %}
		{% endblock%}
		{% endif %}

		<!-- Container -->
		<div id="container">

			{% if not is_popup %}
			{% block adminbar %}
			{% wpadmin_render_top_menu %}
			{% endblock %}
			{% block breadcrumbs %}
			<div class="breadcrumbs">
				<a href="{% url 'admin:index' %}">{% trans 'Home' %}</a>
				{% if title %} &rsaquo; {{ title }}{% endif %}
			</div>
			{% endblock %}
			{% endif %}

			{% block messages %}
			{% if messages %}
			<ul class="messagelist">
				{% for message in messages %}
				<li{% if message.tags %} class="{{ message.tags }}" {% endif %}>{{ message|capfirst }}</li>
					{% endfor %}
			</ul>
			{% endif %}
			{% endblock messages %}

			<!-- Content -->
			<div id="content" class="{% block coltype %}colM{% endblock %}">
				{% block pretitle %}{% endblock %}
				{% block content_title %}{% if title %}<h1>{{ title }}</h1>{% endif %}{% endblock %}
				{% block content %}
				{% block object-tools %}{% endblock %}
				{{ content }}
				{% endblock %}
				{% block sidebar %}{% endblock %}
				<br class="clear" />
			</div>
			<!-- END Content -->

			{% block footer %}<div id="footer"></div>{% endblock %}
		</div>
		<!-- END Container -->

	</div>
	<!-- END Wrapper -->

</body>

</html>
