   272→<body>
   273→    <div class="container">
   274→        <div class="header">
   275→            <h1>문제 관리</h1>
   276→            <p>대회에 포함할 문제를 선택하세요</p>
   277→            <div class="stats">
   278→                <div class="stat-item">전체 문제: <span class="stat-number" id="total-count">0</span>개</div>
   279→                <div class="stat-item">선택된 문제: <span class="stat-number" id="selected-count">0</span>개</div>
   280→            </div>
   281→        </div>
   282→        
   283→        <div class="content-card">
   284→            <div class="header-controls">
   285→                <div class="select-all-container">
   286→                    <input type="checkbox" id="select-all" class="custom-checkbox">
   287→                    <label for="select-all" class="select-all-label">전체 선택</label>
   288→                </div>
   289→            </div>
   290→            
   291→            <div class="problem-list" id="problem-list">
   292→                <!-- 카테고리별 문제 목록이 여기에 동적으로 추가됩니다 -->
   293→            </div>
   294→            
   295→            <div class="actions">
   296→                <div class="select-info">
   297→                    <span id="selection-info">문제를 선택하세요</span>
   298→                </div>
   299→                <div>
   300→                    <button type="button" class="btn btn-secondary" onclick="window.close()">취소</button>
   301→                    <button type="button" class="btn btn-primary" id="save-btn">선택한 문제 적용</button>
   302→                </div>
   303→            </div>
   304→        </div>
   305→    </div>
   306→
   307→    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.6.0/jquery.min.js"></script>
   308→    <script type="text/javascript">
   309→        // Django에서 전달된 문제 데이터
   310→        const problemData = JSON.parse('{{ problems|escapejs }}');
   311→        let problemSelectionDict = {};
   312→        
   313→        // 문제 데이터를 카테고리별로 그룹화
   314→        function groupProblemsByCategory(node, categoryPath = []) {
   315→            const categories = {};
   316→            
   317→            function processNode(currentNode, path) {
   318→                if (!currentNode.children) {
   319→                    // 문제 파일인 경우
   320→                    const categoryName = path.length > 0 ? path.join(' > ') : '기타';
   321→                    if (!categories[categoryName]) {
   322→                        categories[categoryName] = [];
   323→                    }
   324→                    categories[categoryName].push({
   325→                        id: currentNode.id,
   326→                        name: currentNode.name,
   327→                        selected: currentNode.selected
   328→                    });
   329→                } else {
   330→                    // 디렉토리인 경우
   331→                    const newPath = currentNode.name === 'root' ? path : [...path, currentNode.name];
   332→                    currentNode.children.forEach(child => {
   333→                        processNode(child, newPath);
   334→                    });
   335→                }
   336→            }
   337→            
   338→            processNode(node, categoryPath);
   339→            return categories;
   340→        }
   341→        
   342→        // 카테고리별 문제 목록 렌더링
   343→        function renderProblems() {
   344→            const container = document.getElementById('problem-list');
   345→            const categories = groupProblemsByCategory(problemData);
   346→            
   347→            // 데이터가 없는 경우
   348→            if (Object.keys(categories).length === 0) {
   349→                container.innerHTML = `
   350→                    <div class="no-problems">
   351→                        <h3>사용 가능한 문제가 없습니다</h3>
   352→                        <p>문제를 먼저 등록하세요</p>
   353→                    </div>
   354→                `;
   355→                return;
   356→            }
   357→            
   358→            container.innerHTML = '';
   359→            
   360→            // 카테고리별로 렌더링
   361→            Object.entries(categories).forEach(([categoryName, problems], index) => {
   362→                const categoryId = `category-${index}`;
   363→                
   364→                // 카테고리 헤더 생성
   365→                const categoryDiv = document.createElement('div');
   366→                categoryDiv.className = 'category-section';
   367→                categoryDiv.innerHTML = `
   368→                    <div class="category-header" data-category="${categoryId}">
   369→                        <div class="category-left">
   370→                            <span class="category-toggle css-arrow"></span>
   371→                            <input type="checkbox" class="custom-checkbox category-checkbox" data-category="${categoryId}">
   372→                            <span class="category-name">${categoryName}</span>
   373→                        </div>
   374→                        <span class="category-count">${problems.length}개</span>
   375→                    </div>
   376→                    <div class="category-content" id="${categoryId}-content">
   377→                        ${problems.map(problem => {
   378→                            problemSelectionDict[problem.id] = problem.selected;
   379→                            return `
   380→                                <div class="problem-item">
   381→                                    <div class="problem-checkbox-container">
   382→                                        <input type="checkbox" 
   383→                                               class="custom-checkbox problem-checkbox" 
   384→                                               data-problem-id="${problem.id}"
   385→                                               data-category="${categoryId}"
   386→                                               ${problem.selected ? 'checked' : ''}>
   387→                                    </div>
   388→                                    <div class="problem-details">
   389→                                        <span class="problem-name">${problem.name}</span>
   390→                                        <span class="problem-id">ID: ${problem.id}</span>
   391→                                    </div>
   392→                                </div>
   393→                            `;
   394→                        }).join('')}
   395→                    </div>
   396→                `;
   397→                
   398→                container.appendChild(categoryDiv);
   399→            });
   400→            
   401→            updateStats();
   402→        }
   403→        
   404→        // 통계 정보 업데이트
   405→        function updateStats() {
   406→            const totalCount = Object.keys(problemSelectionDict).length;
   407→            const selectedCount = Object.values(problemSelectionDict).filter(selected => selected).length;
   408→            
   409→            document.getElementById('total-count').textContent = totalCount;
   410→            document.getElementById('selected-count').textContent = selectedCount;
   411→            
   412→            const selectionInfo = document.getElementById('selection-info');
   413→            if (selectedCount === 0) {
   414→                selectionInfo.textContent = '문제를 선택하세요';
   415→            } else {
   416→                selectionInfo.textContent = `${selectedCount}개 문제가 선택되었습니다`;
   417→            }
   418→        }
   419→        
   420→        // 이벤트 리스너 설정
   421→        function setupEventListeners() {
   422→            // 전체 선택/해제 체크박스
   423→            const selectAllCheckbox = document.getElementById('select-all');
   424→            selectAllCheckbox.addEventListener('change', function() {
   425→                const isChecked = this.checked;
   426→                document.querySelectorAll('.problem-checkbox').forEach(checkbox => {
   427→                    checkbox.checked = isChecked;
   428→                    const problemId = checkbox.getAttribute('data-problem-id');
   429→                    problemSelectionDict[problemId] = isChecked;
   430→                });
   431→                
   432→                // 카테고리 체크박스도 업데이트
   433→                document.querySelectorAll('.category-checkbox').forEach(categoryCheckbox => {
   434→                    categoryCheckbox.checked = isChecked;
   435→                    categoryCheckbox.indeterminate = false;
   436→                });
   437→                
   438→                updateStats();
   439→            });
   440→            
   441→            // 카테고리 헤더 클릭 (펼치기/접기)
   442→            document.addEventListener('click', function(e) {
   443→                if (e.target.closest('.category-header')) {
   444→                    const header = e.target.closest('.category-header');
   445→                    const categoryId = header.getAttribute('data-category');
   446→                    const content = document.getElementById(categoryId + '-content');
   447→                    const toggle = header.querySelector('.category-toggle');
   448→                    
   449→                    // 체크박스 클릭 시에는 펼치기/접기 동작 방지
   450→                    if (e.target.type === 'checkbox') {
   451→                        return;
   452→                    }
   453→                    
   454→                    if (content.classList.contains('expanded')) {
   455→                        content.classList.remove('expanded');
   456→                        toggle.classList.remove('expanded');
   457→                    } else {
   458→                        content.classList.add('expanded');
   459→                        toggle.classList.add('expanded');
   460→                    }
   461→                }
   462→            });
   463→            
   464→            // 카테고리 체크박스 (카테고리 전체 선택/해제)
   465→            document.addEventListener('change', function(e) {
   466→                if (e.target.classList.contains('category-checkbox')) {
   467→                    const categoryId = e.target.getAttribute('data-category');
   468→                    const isChecked = e.target.checked;
   469→                    
   470→                    // 해당 카테고리의 모든 문제 체크박스 업데이트
   471→                    const categoryProblems = document.querySelectorAll(`.problem-checkbox[data-category="${categoryId}"]`);
   472→                    categoryProblems.forEach(checkbox => {
   473→                        checkbox.checked = isChecked;
   474→                        const problemId = checkbox.getAttribute('data-problem-id');
   475→                        problemSelectionDict[problemId] = isChecked;
   476→                    });
   477→                    
   478→                    updateCategoryCheckboxStates();
   479→                    updateGlobalCheckboxState();
   480→                    updateStats();
   481→                }
   482→            });
   483→            
   484→            // 개별 문제 체크박스
   485→            document.addEventListener('change', function(e) {
   486→                if (e.target.classList.contains('problem-checkbox')) {
   487→                    const problemId = e.target.getAttribute('data-problem-id');
   488→                    problemSelectionDict[problemId] = e.target.checked;
   489→                    
   490→                    updateCategoryCheckboxStates();
   491→                    updateGlobalCheckboxState();
   492→                    updateStats();
   493→                }
   494→            });
   495→            
   496→            // 저장 버튼
   497→            document.getElementById('save-btn').addEventListener('click', function() {
   498→                $.ajax({
   499→                    url: "{% url 'admin:update_contest_inline' contest_id %}",
   500→                    method: 'POST',
   501→                    data: {
   502→                        'selected_items': JSON.stringify(problemSelectionDict),
   503→                        'csrfmiddlewaretoken': '{{ csrf_token }}',
   504→                    },
   505→                    success: function(response) {
   506→                        window.close();
   507→                    },
   508→                    error: function(xhr, status, error) {
   509→                        alert('오류: ' + error);
   510→                    }
   511→                });
   512→            });
   513→        }
   514→        
   515→        // 카테고리 체크박스 상태 업데이트
   516→        function updateCategoryCheckboxStates() {
   517→            document.querySelectorAll('.category-checkbox').forEach(categoryCheckbox => {
   518→                const categoryId = categoryCheckbox.getAttribute('data-category');
   519→                const categoryProblems = document.querySelectorAll(`.problem-checkbox[data-category="${categoryId}"]`);
   520→                const checkedProblems = document.querySelectorAll(`.problem-checkbox[data-category="${categoryId}"]:checked`);
   521→                
   522→                if (checkedProblems.length === 0) {
   523→                    categoryCheckbox.checked = false;
   524→                    categoryCheckbox.indeterminate = false;
   525→                } else if (checkedProblems.length === categoryProblems.length) {
   526→                    categoryCheckbox.checked = true;
   527→                    categoryCheckbox.indeterminate = false;
   528→                } else {
   529→                    categoryCheckbox.checked = false;
   530→                    categoryCheckbox.indeterminate = true;
   531→                }
   532→            });
   533→        }
   534→        
   535→        // 전체 선택 체크박스 상태 업데이트
   536→        function updateGlobalCheckboxState() {
   537→            const selectAllCheckbox = document.getElementById('select-all');
   538→            const allProblems = document.querySelectorAll('.problem-checkbox');
   539→            const checkedProblems = document.querySelectorAll('.problem-checkbox:checked');
   540→            
   541→            if (checkedProblems.length === 0) {
   542→                selectAllCheckbox.checked = false;
   543→                selectAllCheckbox.indeterminate = false;
   544→            } else if (checkedProblems.length === allProblems.length) {
   545→                selectAllCheckbox.checked = true;
   546→                selectAllCheckbox.indeterminate = false;
   547→            } else {
   548→                selectAllCheckbox.checked = false;
   549→                selectAllCheckbox.indeterminate = true;
   550→            }
   551→        }
   552→        
   553→        // 페이지 로드 시 초기화
   554→        document.addEventListener('DOMContentLoaded', function() {
   555→            renderProblems();
   556→            setupEventListeners();
   557→            
   558→            // 초기 체크박스 상태 설정
   559→            updateCategoryCheckboxStates();
   560→            updateGlobalCheckboxState();
   561→        });
   562→    </script>
   563→</body>
   564→</html>

