    99→        document.addEventListener('DOMContentLoaded', function() {
   100→            var popupWindow = null;
   101→            // 버튼 생성 함수
   102→            function createButton() {
   103→                var button = document.createElement('button');
   104→                button.type = 'button';
   105→                button.innerText = '문제 관리';
   106→                button.classList.add('open-modal-btn');
   107→                button.onclick = function openPopup() {
   108→                    // 팝업 창 크기 및 위치 설정
   109→                    var width = 1000;
   110→                    var height = 700;
   111→                    var left = (screen.width - width) / 2;
   112→                    var top = (screen.height - height) / 2;
   113→                    
   114→                    var popupOptions = "width=" + width + ",height=" + height + ",left=" + left + ",top=" + top + ",scrollbars=yes,resizable=yes,status=no,menubar=no,toolbar=no";
   115→                    
   116→                    const popupWindow = window.open("{% url 'admin:problem_manager' original.pk %}", "ProblemManager", popupOptions);
   117→
   118→                    // 주기적으로 팝업 창의 상태를 확인하여 닫혔는지 체크
   119→                    var popupCheckInterval = setInterval(function() {
   120→                        if (popupWindow.closed) {
   121→                            clearInterval(popupCheckInterval);
   122→                            location.reload(); // 페이지 새로고침
   123→                        }
   124→                    }, 500); // 0.5초마다 체크
   125→                }
   126→                return button;
